    users.hasMany(Message, {
      as: 'sentMessages',
      foreignKey: 'senderId',
    });

    users.hasMany(Message, {
      as: 'receivedMessages',
      foreignKey: 'receiverId',
    });

    users.belongsToMany(Chat, {
      as: 'chats',
      through: ChatUser,
      foreignKey: 'userId',
    });

  };
  return users;
};

